mapper.select:
select mr.house_id as houseId, mr.id as mrId, mr.owner_name as ownerName, mr.worker_type_id as workerTypeId, mrrp.proportion AS proportion, mrrp.id as mrrpId, mrrp.responsible_party_id AS responsiblePartyId, mrrp.maintenance_total_price as maintenanceTotalPrice, h.square as square, mrrp.total_price as totalPrice, CONCAT( h.residential, IFNULL(h.building, '*'), '栋', IFNULL(CONCAT(h.unit, '单元'),'' ), IFNULL(h.number, '*'), '号' ) houseName, (case when mrrp.create_date>=DATE_ADD(NOW(),INTERVAL -7 DAY) THEN 1 else 0 end ) isComplain, IFNULL(mrrp.stevedorage_cost,0) stevedorageCost, IFNULL(mrrp.transportation_cost,0) transportationCost from dj_maintenance_record mr INNER JOIN dj_maintenance_record_responsible_party mrrp on mrrp.maintenance_record_id = mr.id INNER JOIN dj_house h on mr.house_id = h.id where 1=1
and mrrp.id = #{mrrpId}
and mrrp.responsible_party_type = #{type}
