mapper.select:
SELECT id, create_date as createDate, feedback_type as feedbackType, app_type as appType, user_id as userId, feedback_id AS feedbackId from dj_user_feedback where 1=1
and app_type = #{appType}
and feedback_type = #{feedbackType}
and create_date between #{beginDate} and #{endDate}
ORDER BY create_date DESC
