mapper.select:
SELECT house.id, house.member_id as memberId, house.residential, house.building, house.unit, house.number, house.data_status as dataStatus, house.create_date as createDate, house.construction_date as constructionDate, house.modify_date as modifyDate, (SELECT COUNT(*) from dj_deliver_order_split where apply_status = 1 and house_id = house.id) as waitCount, (SELECT COUNT(*) from dj_deliver_order_split where apply_status = 2 and house_id = house.id) as sendCount FROM dj_house as house WHERE 1=1
and house.city_id = #{cityId}
and house.construction_date BETWEEN #{startDate} and #{endDate}
AND CONCAT( house.residential, IFNULL(house.building, '*'), '栋', IFNULL(CONCAT(house.unit, '单元'),'' ), IFNULL(house.number, '*'), '号' ) LIKE CONCAT('%',#{likeAddress},'%')
ORDER BY waitCount DESC,sendCount DESC,house.create_date DESC

SELECT
, ( SELECT ( st_distance ( point (v.locationx, v.locationy), point (mv.locationx, mv.locationy) ) * 111195 / 1000 ) AS juli FROM `dj_house_modeling_village` mv WHERE mv.locationx IS NOT NULL AND mv.id = #{villageId} ) juli
from dj_house h INNER JOIN `dj_house_modeling_village` v ON h.village_id = v.id INNER JOIN (SELECT a.house_id,COUNT(a.id) num FROM `dj_design_quantity_room_images` a GROUP BY a.house_id ) b ON b.house_id=h.id where h.data_status=0
and h.city_id = #{cityId}
and h.build_square >= #{minSquare}
and h.build_square <= #{maxSquare}
and h.house_type = #{houseType}
and h.site_display=0 AND h.visit_state in(1,3)
ORDER BY FIELD(h.village_id,#{villageId}) DESC,juli,b.num DESC
order by b.num desc

SELECT

from dj_house
where
data_status=0

    and city_id = #{cityId}


    and village_id = #{villageId}

and build_square >= #{minSquare}
and build_square <= #{maxSquare}
and house_type = #{houseType}
and budget_ok = 3
