mapper.select:
SELECT

from dj_house h
INNER JOIN (SELECT a.house_id,COUNT(a.id) num FROM dj_core_house_flow_apply a GROUP BY a.house_id ) b ON
b.house_id=h.id
where
data_status=0

    and city_id = #{cityId}


    and village_id = #{villageId}


    and build_square >= #{minSquare}


    and build_square <= #{maxSquare}


    and house_type = #{houseType}

and ((decoration_type in (1,2) and budget_ok=3) or (decoration_type=3 and visit_state=1))
and site_display=0
order by num desc

SELECT

from dj_house
where
data_status=0

    and city_id = #{cityId}


    and village_id = #{villageId}

and build_square >= #{minSquare}
and build_square <= #{maxSquare}
and house_type = #{houseType}
and budget_ok = 3

SELECT h.id as houseId, h.city_name as cityName, m.id as memberId, m.nick_name as memberName, m.mobile as mobile, h.visit_state as visitState, h.show_house as showHouse, h.style as style, h.square as square, h.build_square as buildSquare, h.decoration_type as decorationType, h.house_type as houseType, h.residential as residential, h.building as building, h.unit as unit, h.work_deposit_id as workDepositId, h.create_date AS createDate, h.modify_date AS modifyDate, h.city_id as cityId, h.modeling_layout_id as modelingLayoutId, h.village_id as villageId, h.number as number, h.site_display as siteDisplay FROM dj_house h INNER JOIN dj_member m ON h.member_id = m.id WHERE h.data_status =0
and m.id = #{memberId}
and h.visit_state=#{visitState}
and h.create_date BETWEEN #{startDate} and #{endDate}
AND ( m.mobile like CONCAT('%',#{searchKey},'%') or CONCAT( h.residential, IFNULL(h.building, '*'), '栋', IFNULL(CONCAT(h.unit, '单元'),'' ), IFNULL(h.number, '*'), '号' ) LIKE CONCAT('%',#{searchKey},'%') )
ORDER by h.create_date
desc
