mapper.select:
SELECT

from dj_house h
INNER JOIN (SELECT a.house_id,COUNT(a.id) num FROM dj_core_house_flow_apply a GROUP BY a.house_id ) b ON
b.house_id=h.id
where
data_status=0

    and city_id = #{cityId}


    and village_id = #{villageId}


    and build_square >= #{minSquare}


    and build_square <= #{maxSquare}


    and house_type = #{houseType}

and ((decoration_type in (1,2) and budget_ok=3) or (decoration_type=3 and visit_state=1))
and site_display=0
order by num desc

SELECT
from dj_house h INNER JOIN (SELECT a.house_id,COUNT(a.id) num FROM dj_core_house_flow_apply a GROUP BY a.house_id ) b ON b.house_id=h.id INNER JOIN ( SELECT mv.*, ( st_distance ( point (mv.locationx, mv.locationy), point (#{locationx} ,#{locationy}) ) * 111195 / 1000 ) AS juli FROM `dj_house_modeling_village` mv WHERE mv.locationx IS NOT NULL AND mv.id=#{villageId} ) v ON v.id = h.village_id where h.data_status=0
and h.city_id = #{cityId}
and h.build_square >= #{minSquare}
or h.build_square <= #{maxSquare}
and h.show_house=0 and h.visit_state=3 and h.site_display=0 ORDER BY v.juli ASC

SELECT

from dj_house
where
data_status=0

    and city_id = #{cityId}


    and village_id = #{villageId}

and build_square >= #{minSquare}
and build_square <= #{maxSquare}
and house_type = #{houseType}
and budget_ok = 3

SELECT h.id as houseId, h.city_name as cityName, m.id as memberId, m.nick_name as memberName, m.mobile as mobile, h.visit_state as visitState, h.show_house as showHouse, h.style as style, h.square as square, h.build_square as buildSquare, h.decoration_type as decorationType, h.house_type as houseType, h.residential as residential, h.building as building, h.unit as unit, h.work_deposit_id as workDepositId, h.create_date AS createDate, h.modify_date AS modifyDate, h.construction_date as constructionDate, h.city_id as cityId, h.modeling_layout_id as modelingLayoutId, h.village_id as villageId, h.number as number, h.site_display as siteDisplay FROM dj_house h INNER JOIN dj_member m ON h.member_id = m.id WHERE h.data_status =0
AND FIND_IN_SET(h.city_id,#{cityKey})
and m.id = #{memberId}
and h.visit_state=#{visitState}
and h.create_date BETWEEN #{startDate} and #{endDate}
AND ( m.mobile like CONCAT('%',#{searchKey},'%') or CONCAT( h.residential, IFNULL(h.building, '*'), '栋', IFNULL(CONCAT(h.unit, '单元'),'' ), IFNULL(h.number, '*'), '号' ) LIKE CONCAT('%',#{searchKey},'%') )
ORDER by h.create_date
desc

SELECT h.id as houseId, CONCAT( h.residential, IFNULL(h.building, '*'), '栋', IFNULL(CONCAT(h.unit, '单元'),'' ), IFNULL(h.number, '*'), '号' ) houseName, h.city_name as cityName, h.budget_ok as budgetOk, h.designer_ok as designerOk, h.custom_sort as customSort, m.nick_name as name, m.id as memberId, m.name as memberName, m.mobile as mobile, h.visit_state as visitState, h.show_house as showHouse, h.style as style, h.square as square, h.build_square as buildSquare, h.decoration_type as decorationType, h.house_type as houseType, h.residential as residential, h.building as building, h.unit as unit, h.work_deposit_id as workDepositId, h.create_date AS createDate, h.modify_date AS modifyDate, h.construction_date as constructionDate, h.number as number, h.optional_label as optionalLabel, ( CASE WHEN (h.decoration_type = 2 AND h.designer_ok != 3) THEN 1 ELSE 0 END ) AS showUpdata FROM dj_house h INNER JOIN dj_member m ON h.member_id = m.id WHERE (h.decoration_type = 2 or (h.decoration_type != 2 and h.designer_ok =3))
AND h.data_status=#{dataStatus}
and (budget_ok =1 or budget_ok = 4)
and budget_ok =#{budgetOk}
AND ( m.mobile like CONCAT('%',#{searchKey},'%') or CONCAT( h.residential, IFNULL(h.building, '*'), '栋', IFNULL(CONCAT(h.unit, '单元'),'' ), IFNULL(h.number, '*'), '号' ) LIKE CONCAT('%',#{searchKey},'%') )
ORDER by h.create_date desc
