mapper.select:
SELECT * FROM (SELECT

,ROUND(
6378.138 * 2
* ASIN(
SQRT(
POW(
SIN(
(
#{latitude} * PI() / 180 - mv.locationy * PI() / 180
) / 2
)
,
2
) + COS(
#{latitude} * PI() / 180) * COS(mv.locationy * PI() / 180) * POW(
SIN(
(
#{longitude} * PI() / 180 - mv.locationx * PI() / 180
) / 2
),
2
)
)
) * 1000
) AS juli
FROM
`dj_house_modeling_village` mv ,dj_house h
inner join
(SELECT a.house_id,COUNT(a.id) num FROM dj_core_house_flow_apply a GROUP BY a.house_id ) b ON
b.house_id=h.id/*筛选调无施工记录的房子*/
WHERE
mv.id=h.village_id
AND mv.locationx IS NOT NULL
AND ((h.decoration_type in (1,2) and h.budget_ok=3) OR (h.decoration_type=3 and h.visit_state=1))
AND h.visit_state in(1,3)
AND h.site_display=0
AND ROUND(
6378.138 * 2
* ASIN(
SQRT(
POW(
SIN(
(
#{latitude} * PI() / 180 - mv.locationy * PI() / 180
) / 2
)
,
2
) + COS(
#{latitude} * PI() / 180) * COS(mv.locationy * PI() / 180) * POW(
SIN(
(
#{longitude} * PI() / 180 - mv.locationx * PI() / 180
) / 2
),
2
)
)
) * 1000
) <=(#{endDistance}*1000)
AND
ROUND(
6378.138 * 2
* ASIN(
SQRT(
POW(
SIN(
(
#{latitude} * PI() / 180 - mv.locationy * PI() / 180
) / 2
)
,
2
) + COS(
#{latitude} * PI() / 180) * COS(mv.locationy * PI() / 180) * POW(
SIN(
(
#{longitude} * PI() / 180 - mv.locationx * PI() / 180
) / 2
),
2
)
)
) * 1000
) >=(#{beginDistance}*1000)
ORDER BY juli,h.construction_date DESC
)tab
order by rand() limit 2

SELECT * FROM (SELECT

,ROUND(
6378.138 * 2
* ASIN(
SQRT(
POW(
SIN(
(
#{latitude} * PI() / 180 - mv.locationy * PI() / 180
) / 2
)
,
2
) + COS(
#{latitude} * PI() / 180) * COS(mv.locationy * PI() / 180) * POW(
SIN(
(
#{longitude} * PI() / 180 - mv.locationx * PI() / 180
) / 2
),
2
)
)
) * 1000
) AS juli
FROM
`dj_house_modeling_village` mv ,dj_house h
WHERE
mv.id=h.village_id
AND mv.locationx IS NOT NULL
AND ((h.decoration_type in (1,2) and h.budget_ok=3) OR (h.decoration_type=3 and h.visit_state=1))
AND h.visit_state in(1,3)
AND h.site_display=0
ORDER BY juli,h.construction_date DESC limit 20
)tab
order by rand() limit #{limit}
