mapper.select:
SELECT

FROM dj_house_surplus_ware_house_item
where 1=1

	and surplus_ware_house_id = #{surplusWareHouseId}


	and product_id = #{productId}

ORDER BY create_date desc

SELECT wh.address, whi.product_name as productName, whi.product_count as productCount FROM dj_house_surplus_ware_house as wh INNER JOIN dj_house_surplus_ware_house_item as whi on (wh.id = whi.surplus_ware_house_id ) WHERE wh.data_status=0
and product_id = #{productId}
ORDER BY wh.category_id desc
