mapper.select:
        SELECT

        FROM dj_house_warehouse
        WHERE
		    product_id=#{productId}
		and
		    house_id=#{houseId}
		LIMIT 1;

SELECT
FROM dj_house_warehouse where 1=1 and house_id = #{houseId}
and product_name LIKE CONCAT('%',#{name},'%')
and category_id = #{categoryId}
ORDER BY create_date desc
