mapper.select:
		select DISTINCT bm.category_id
		from dj_house_warehouse bm
 		where bm.house_id = #{houseId}
 		and bm.category_id !=''
 		and bm.category_id is not null;

SELECT
FROM dj_house_warehouse where house_id = #{houseId}
and product_name LIKE CONCAT('%',#{name},'%')
and category_id = #{categoryId}
ORDER BY create_date desc

mapper.update:
UPDATE dj_house_warehouse p1
SET p1.product_name =

	when id then #{item.name}

,p1.unit_name=

	when id then #{item.unitName}

,p1.image=

	when id then #{item.image}

where product_id in

	#{item.id,jdbcType=BIGINT}
