mapper.select:
SELECT

FROM dj_member_customer
WHERE 1=1

    and member_id=#{memberId}


    and stage=#{stage}

ORDER by create_date desc

mapper.update:
update dj_member_customer
set label_id_arr = #{labelIdArr}
where member_id = #{memberId}

update dj_clue
set label_id = #{labelId}
where id = #{clueId}
