mapper.select:
SELECT

FROM dj_member w
where w.data_status = 0

    AND password=#{password}


    AND mobile=#{mobile}



    AND smscode=#{smscode}

LIMIT 1

SELECT COUNT(o.id) FROM `dj_core_house_worker` o

    INNER JOIN dj_house h ON h.id = o.house_id
    WHERE
    o.worker_id = #{workerId}
    AND o.work_type NOT IN (2, 3, 4, 5, 7)

        and o.create_date BETWEEN #{startDate} and #{endDate}



    INNER JOIN `dj_deliver_order` h ON h.house_id = o.house_id
    AND h.worker_type_id = o.worker_type_id
    WHERE
    o.worker_id = #{workerId}
    AND o.work_type = 6

        and h.create_date BETWEEN #{startDate} and #{endDate}



    INNER JOIN dj_house h ON h.id = o.house_id
    WHERE
    o.worker_id = #{workerId}
    AND o.work_type = 6
    AND h.budget_ok in (2,3,4)

        and o.create_date BETWEEN #{startDate} and #{endDate}



    INNER JOIN dj_house h ON h.id = o.house_id
    WHERE
    o.worker_id = #{workerId}
    AND o.work_type = 6
    AND h.budget_ok = 3

        and o.create_date BETWEEN #{startDate} and #{endDate}



    INNER JOIN dj_house h ON h.id = o.house_id
    INNER JOIN dj_core_house_worker o2 ON o2.house_id = o.house_id
    WHERE
    o.worker_id = #{workerId}
    AND o.work_type = 6
    AND o2.work_type = 6
    AND o2.worker_type = 3
    AND h.budget_ok = 3
    AND h.construction_date IS NOT NULL

        and o.create_date BETWEEN #{startDate} and #{endDate}



    INNER JOIN dj_house h ON h.id = o.house_id
    WHERE
    o.worker_id = #{workerId}
    AND o.work_type NOT IN (2, 3, 4, 5, 7)
    AND h.visit_state = 4

        and h.completed_date BETWEEN #{startDate} and #{endDate}

SELECT COUNT(o.id) FROM `dj_core_house_worker` o
INNER JOIN dj_house h ON h.id = o.house_id WHERE o.worker_id = #{workerId} AND o.work_type NOT IN (2, 3, 4, 5, 7)
and o.create_date BETWEEN #{startDate} and #{endDate}
INNER JOIN `dj_deliver_order` h ON h.house_id = o.house_id AND h.worker_type_id = o.worker_type_id WHERE o.worker_id = #{workerId} AND o.work_type = 6
and h.create_date BETWEEN #{startDate} and #{endDate}
INNER JOIN ( SELECT h.id, r.create_date FROM dj_house h INNER JOIN dj_design_quantity_room r ON r.house_id = h.id WHERE r.type = 0 AND h.designer_ok IN ( 9, 5, 6, 7, 2, 8, 3 ) GROUP BY h.id ) h ON h.id = o.house_id WHERE o.worker_id = #{workerId}
and h.create_date BETWEEN #{startDate} and #{endDate}
INNER JOIN ( SELECT h.id, r.create_date FROM dj_house h INNER JOIN dj_design_quantity_room r ON r.house_id = h.id WHERE r.type = 1 AND h.designer_ok IN (5, 6, 7, 2, 8, 3 ) GROUP BY h.id ) h ON h.id = o.house_id WHERE o.worker_id = #{workerId}
and h.create_date BETWEEN #{startDate} and #{endDate}
INNER JOIN ( SELECT h.id, r.create_date FROM dj_house h INNER JOIN dj_design_quantity_room r ON r.house_id = h.id WHERE r.type = 1 AND h.designer_ok IN (7, 2, 8, 3 ) GROUP BY h.id ) h ON h.id = o.house_id WHERE o.worker_id = #{workerId}
and h.create_date BETWEEN #{startDate} and #{endDate}
INNER JOIN ( SELECT h.id, r.create_date FROM dj_house h INNER JOIN dj_design_quantity_room r ON r.house_id = h.id WHERE r.type = 2 AND h.designer_ok IN (2, 8, 3 ) GROUP BY h.id ) h ON h.id = o.house_id WHERE o.worker_id = #{workerId}
and h.create_date BETWEEN #{startDate} and #{endDate}
INNER JOIN ( SELECT h.id, r.create_date FROM dj_house h INNER JOIN dj_design_quantity_room r ON r.house_id = h.id WHERE r.type = 2 AND h.designer_ok = 3 GROUP BY h.id ) h ON h.id = o.house_id WHERE o.worker_id = #{workerId}
and h.create_date BETWEEN #{startDate} and #{endDate}
INNER JOIN dj_house h ON h.id = o.house_id AND h.budget_ok in (1, 2, 3, 4) AND h.designer_ok = 3 WHERE o.worker_id = #{workerId}
and o.create_date BETWEEN #{startDate} and #{endDate}
INNER JOIN dj_house h ON h.id = o.house_id WHERE o.worker_id = #{workerId} AND o.work_type NOT IN (2, 3, 4, 5, 7) AND h.visit_state = 4
and h.completed_date BETWEEN #{startDate} and #{endDate}
