mapper.select:
SELECT

FROM dj_pay_business_order
where task_id = #{taskId}
and type = #{type}
and state !=4

SELECT a.id AS id, a.number AS orderId, m.address AS houseName, m.member_id AS memberId, m.mobile AS mobile, a.pay_order_number AS payOrderNumber, a.total_price AS totalAmount, a.discounts_price AS redPackAmount, a.pay_price AS actualPayment, a.type AS type, a.image , a.storefront_id as storefrontId, a.state AS state, a.task_id AS taskId, c.village_id storeName, a.red_packet_pay_money_id redPackId, a.discounts_price redPackAmount FROM dj_pay_business_order AS a INNER JOIN dj_deliver_order t on a.number=t.business_order_number INNER JOIN dj_member_address m ON (t.address_id=m.id or t.house_id=m.house_id) LEFT JOIN dj_house c on a.house_id=c.id
INNER JOIN dj_pay_pay_order pa AS pa ON pa.number = a.pay_order_number
WHERE a.data_status = 0 and m.city_id=#{cityId}
and pa.create_date between #{beginDate} and #{endDate}
and a.state = #{state}
and ( m.mobile like CONCAT('%',#{searchKey},'%') or m.address LIKE CONCAT('%',#{searchKey},'%') or a.pay_order_number like CONCAT('%',#{searchKey},'%') or a.number like CONCAT('%',#{searchKey},'%'))
group by a.id ORDER BY a.create_date DESC
