mapper.select:
SELECT c.id productId, c.name productName, c.product_sn productSn, c.image FROM dj_basics_product_template c, dj_basics_goods g WHERE c.city_id=#{cityId}
and c.category_id=#{categoryId}
and c.goods_id=g.id and c.maket=1 and c.type=1 and c.data_status=0
and (c.name like CONCAT('%',#{searchKey},'%') or g.name like CONCAT('%',#{searchKey},'%'))

SELECT p.id productId, p.product_name productName, c.product_sn productSn, p.image FROM dj_basics_product_template c, dj_basics_goods g, dj_basics_storefront_product p WHERE c.goods_id=g.id
and c.category_id=#{categoryId}
and c.data_status=0 and c.id=p.prod_template_id and p.is_shelf_status=1 and p.storefront_id=#{storefrontId}
and (p.name like CONCAT('%',#{searchKey},'%') or g.name like CONCAT('%',#{searchKey},'%'))
