SELECT a.id, a.price, a.shop_count as shopCount, a.unit_name as unitName, a.product_type as productType, a.storefront_id as storefrontId , b.id AS productId, b.product_name AS productName, c.product_sn as productSn, b.image, b.sell_price AS sellPrice, d.is_reservation_deliver AS isReservationDeliver, e.type, e.type as unitType, c.value_name_arr as valueNameArr, e.type as unitType, c.category_id as categoryId, d.type as productType FROM dj_repair_shopping_cart a INNER JOIN dj_basics_storefront_product b ON a.product_id = b.id INNER JOIN dj_basics_product_template c ON b.prod_template_id = c.id INNER JOIN dj_basics_goods d ON b.goods_id = d.id INNER JOIN dj_basics_unit e ON c.convert_unit=e.id WHERE a.member_id =#{memberId} AND a.city_id =#{cityId} AND a.storefront_id=#{storefrontId} AND d.type < 4
AND a.product_id in
#{item}
