mapper.select:
SELECT id, number AS number, create_date AS createDate, total_amount AS totalAmount, apply_money AS applyMoney, apply_state AS applyState, ship_address AS shipAddress, supplier_id AS supplierId FROM dj_repair_mend_deliver WHERE data_status = 0 and apply_state IS not NULL AND shipping_state=1 AND supplier_id=#{supplierId}
and apply_state =0
and modify_date between #{beginDate} and #{endDate}
and ship_address like CONCAT('%',#{shipAddress},'%')
ORDER BY create_date DESC

SELECT id, number AS number, create_date AS createDate, total_amount AS totalAmount, apply_money AS applyMoney, apply_state AS applyState, ship_address AS shipAddress, supplier_id AS supplierId FROM dj_repair_mend_deliver WHERE id=#{id}
and modify_date between #{beginDate} and #{endDate}
and ship_address like CONCAT('%',#{shipAddress},'%')
