mapper.select:
SELECT id, number AS number, back_time AS createDate, total_amount AS totalAmount, apply_money AS applyMoney, apply_state AS applyState, ship_address AS shipAddress, supplier_id AS supplierId FROM dj_repair_mend_deliver WHERE data_status = 0 and apply_state IS not NULL AND supplier_id=#{supplierId}
and apply_state =0
AND (apply_state = 1 OR apply_state = 2)
and modify_date between #{beginDate} and #{endDate}
and ship_address like CONCAT('%',#{shipAddress},'%')
ORDER BY create_date DESC

SELECT id, number AS number, create_date AS createDate, total_amount AS totalAmount, apply_money AS applyMoney, apply_state AS applyState, ship_address AS shipAddress, supplier_id AS supplierId FROM dj_repair_mend_deliver WHERE id=#{id}
and modify_date between #{beginDate} and #{endDate}
and ship_address like CONCAT('%',#{shipAddress},'%')

SELECT count(t.id) FROM dj_repair_mend_order t, dj_member m, dj_member_address madress, dj_repair_mend_deliver md WHERE t.apply_member_id=m.id and t.id=md.mend_order_id and ( t.house_id = madress.house_id OR t.address_id = madress.id ) and t.storefront_id=#{storefrontId}
and md.shipping_state in(0,4,5)
and md.shipping_state in(1,2,6,7,8)
