mapper.select:
SELECT id, number AS number, create_date AS createDate, total_amount AS totalAmount, apply_money AS applyMoney, apply_state AS applyState, ship_address AS shipAddress FROM dj_repair_mend_deliver WHERE data_status = 0 and apply_state IS not NULL AND supplier_id=#{supplierId}
and apply_state IN (0,1)
and apply_state =2
and modify_date between #{beginDate} and #{endDate}
and ship_address like CONCAT('%',#{shipAddress},'%')
ORDER BY create_date DESC
