mapper.select:
SELECT

FROM dj_repair_mend_order
where
type = #{type}
and state > 0
and house_id = #{houseId}

    and
    worker_type_id = #{workerTypeId}

ORDER BY create_date desc

SELECT rmo.id, rmo.number, rmo.change_order_id as changeOrderId, rmo.image_arr as imageArr, rmo.business_order_number as businessOrderNumber, rmo.order_name as orderName, rmo.house_id as houseId, rmo.worker_type_id as workerTypeId, rmo.apply_member_id as applyMemberId, rmo.type, rmo.state, rmo.total_amount as totalAmount, rmo.carriage, rmo.data_status as dataStatus, rmo.create_date as createDate, rmo.modify_date as modifyDate, house.id, house.residential as residential, house.building as building, house.unit as unit, house.number as number, house.member_id as memberId FROM dj_repair_mend_order as rmo left JOIN dj_house as house on (rmo.house_id = house.id ) where 1=1 and rmo.state > 0
and rmo.storefront_id = #{storefrontId}
and rmo.house_id = #{houseId}
and rmo.type = #{type}
and rmo.create_date between #{beginDate} and #{endDate}
AND CONCAT( house.residential, IFNULL(house.building, '*'), '栋', IFNULL(CONCAT(house.unit, '单元'),'' ), IFNULL(house.number, '*'), '号' ) LIKE CONCAT('%',#{likeAddress},'%')
ORDER BY rmo.create_date desc
