mapper.select:
select COUNT(*) from dj_clue c inner join dj_house h on c.member_id = h.member_id where 1 =1 and c.cus_service in (SELECT su.user_id from dj_store_users su where 1=1
and su.store_id = #{storeId}
) and h.visit_state = 1
and date_format(h.modify_date,'%Y-%m') = #{time}

select c.cus_service userId, h.visit_state visitState from dj_clue c inner join dj_house h on c.member_id = h.member_id where c.cus_service in (SELECT su.user_id from dj_store_users su where 1=1
and su.store_id = #{storeId}
) and (h.visit_state = 1 or h.visit_state = 3)

select  c.cus_service as userId,h.visit_state as visitState
from
dj_clue c  inner join dj_house h on c.member_id = h.member_id
where c.cus_service in
(SELECT su.user_id from dj_store_users su
    where 1=1

        and su.store_id = #{storeId}

 )

    and date_format(h.modify_date,'%Y-%m') =#{time}

and (h.visit_state = 1 or h.visit_state = 3)

select c.cus_service as userId,count(*) as count,h.visit_state as visitState from dj_clue c inner join dj_house h on c.member_id = h.member_id where c.cus_service in (SELECT su.user_id from dj_store_users su where 1=1
and su.store_id = #{storeId}
) and (h.visit_state = 1 or h.visit_state = 3) and (h.visit_state = 1 or h.visit_state = 3) group by cus_service having count>0

select h.id as id, h.residential as residential, h.building as building, h.unit as unit, h.number as number, h.visit_state as visitState, rm.user_id as userId, rm.order_status as orderStatus, rm.month_royalty as monthRoyalty, rm.meter_royalty as meterRoyalty, rm.arr_royalty as arrRoyalty from dj_royalty_match rm inner join dj_house h on rm.house_id=h.id where 1=1
and rm.user_id = #{userId}
and date_format(rm.create_date,'%Y-%m') = #{time}
and h.visit_state = #{visitState}
and h.village_id=#{villageId}
ORDER BY rm.create_date desc

SELECT
    count(0)
FROM
    dj_royalty_match rm
INNER JOIN dj_house h
ON rm.house_id = h.id
WHERE
    user_id =#{userId}
AND order_status = 0
AND date_format(rm.create_date,'%Y-%m')=#{time}

    and h.building=#{building}


    and h.village_id=#{villageId}

SELECT
    h.id,
    CONCAT(
        IFNULL(h.residential, '*'),
        IFNULL(CONCAT(h.building, '栋'),'*栋'),
        IFNULL(CONCAT(h.unit, '单元'),'*单元'),
        IFNULL(CONCAT(h.number, '号'),'*号')
    ) AS houseName,
    h.visit_state as visitState
FROM
    dj_royalty_match rm
INNER JOIN dj_house h ON rm.house_id = h.id
where 1=1

    and rm.user_id = #{userId}


    and date_format(rm.create_date,'%Y-%m') = #{time}


    and h.visit_state = #{visitState}


    and h.building=#{building}


    and h.village_id=#{villageId}

GROUP BY h.id
ORDER BY h.modify_date desc
