mapper.select:
        select
        dj.id as id,
        dj.user_id as userId
		from dj_already_rob_single dj
		where dj.user_id = #{userId}
		and DATE_FORMAT(create_date, '%Y-%m') = #{createDate}

mapper.update:
UPDATE dj_already_rob_single
SET
    data_status = #{dataStatus}
WHERE
house_id = #{houseId}
