mapper.select:
select id, create_date as createDate, modify_date as modifyDate, data_status as dataStatus, village_id as villageId, building, store_id as storeId from dj_sale_residential_building WHERE 1=1
and store_id=#{storeId}
and building LIKE CONCAT('%',#{building},'%') and village_id=#{villageId} LIMIT 1

select id, create_date as createDate, modify_date as modifyDate, data_status as dataStatus, village_id as villageId, building, store_id as storeId from dj_sale_residential_building WHERE id in
#{item}
Group by village_id

mapper.update:
update dj_sale_residential_building
set store_id=#{storeId}
where village_id in

    #{villageIds}
