mapper.select:
SELECT
,
ROUND(
    6378.138 * 2 * ASIN(
    SQRT(
        POW(
            SIN(
                (
                28.189381 * PI() / 180 - latitude * PI() / 180
                ) / 2
                ),
                2
                ) + COS(28.189381 * PI() / 180) * COS(latitude * PI() / 180) * POW(
                SIN(
                (
                112.841035 * PI() / 180 - longitude * PI() / 180
                ) / 2
                ),
                2
            )
        )
    ) * 1000
) AS juli
FROM dj_store
where data_status =0

    AND city_id=#{cityId}


    AND store_name=#{store_name}

ORDER BY
juli

SELECT
,
ROUND(
    6378.138 * 2 * ASIN(
    SQRT(
    POW(
    SIN(
    (
    #{latitude} * PI() / 180 - latitude * PI() / 180
    ) / 2
    ),
    2
    ) + COS(
    #{latitude} * PI() / 180) * COS(latitude * PI() / 180) * POW(
    SIN(
    (
    #{longitude} * PI() / 180 - longitude * PI() / 180
    ) / 2
    ),
    2
    )
    )
    ) * 1000
) AS juli
FROM dj_store
where data_status =0
ORDER BY
juli limit 1
