mapper.select:
SELECT su.id AS storeUserId, su.user_id AS userId, su.store_id AS storeId, su.type AS type, u.username AS userName, u.mobile AS userMobile, m.head AS userHead, u.is_job AS isJob, su.create_date AS createDate, su.modify_date AS modifyDate FROM dj_store_users su INNER JOIN dj_user u ON su.user_id = u.id LEFT JOIN dj_member m ON u.member_id = m.id WHERE su.data_status = 0
AND ( u.username LIKE CONCAT('%',#{searchKey},'%') OR u.mobile LIKE CONCAT('%',#{searchKey},'%') )
AND su.store_id = #{storeId}
ORDER BY su.create_date DESC
LIMIT #{limit}
