mapper.select:
SELECT IFNULL( ma.address, CONCAT( h.residential, IFNULL( h.building, '*' ), '栋', IFNULL( CONCAT( h.unit, '单元' ), '' ), IFNULL( h.number, '*' ), '号' ) ) houseName, h.id AS houseId, m.nick_name AS memberName, m.mobile AS memberPhone, m.id AS memberId, h.construction_date constructionDate, ( CASE WHEN ( f.supervisor_start = 1 OR f.supervisor_start = 0 ) AND h.visit_state != 2 AND h.visit_state != 3 AND h.visit_state != 4 AND h.visit_state != 5 AND h.pause = 0 THEN f.supervisor_start WHEN h.visit_state = 3 THEN 3 WHEN h.visit_state = 4 THEN 4 WHEN h.visit_state = 5 THEN 5 WHEN ( h.visit_state = 2 OR h.pause = 1 ) THEN 2 END ) AS visitState FROM dj_basics_supervisor_authority sa INNER JOIN dj_house h on sa.house_id = h.id INNER JOIN ( SELECT f.house_id, f.supervisor_start FROM dj_core_house_flow f LEFT JOIN dj_member m ON f.worker_id = m.id WHERE f.worker_type = 3 AND f.work_type > 1 ) f ON h.id = f.house_id INNER JOIN dj_member m ON h.member_id = m.id LEFT JOIN dj_member_address ma ON h.id = ma.house_id WHERE h.data_status = 0 and sa.data_status =0
AND sa.member_id = #{memberId}
and f.supervisor_start = #{visitState} and h.visit_state !=2 and h.visit_state !=3 and h.visit_state !=4 and h.visit_state !=5 and h.pause=0
and h.visit_state = #{visitState}
and ( m.mobile like CONCAT('%',#{searchKey},'%') or CONCAT( h.residential, IFNULL(h.building, '*'), '栋', IFNULL(CONCAT(h.unit, '单元'),'' ), IFNULL(h.number, '*'), '号' ) LIKE CONCAT('%',#{searchKey},'%') or m.nick_name like CONCAT('%',#{searchKey},'%') or m.name like CONCAT('%',#{searchKey},'%') or ma.address like CONCAT('%',#{searchKey},'%') )
ORDER BY h.modify_date DESC

SELECT count( 1 ) AS num, '全部' AS NAME, 1 AS sortNum, 'iconWork/home_icon_quanbu.png' AS image FROM dj_basics_supervisor_authority sa INNER JOIN dj_house h ON sa.house_id = h.id WHERE h.data_status = 0 AND sa.member_id = #{memberId}
AND h.city_id = #{cityId}
UNION ALL SELECT count( 1 ) AS num, '在施工地' AS NAME, 2 AS sortNum, 'iconWork/home_icon_zaishigong.png' AS image FROM dj_basics_supervisor_authority sa INNER JOIN dj_house h ON sa.house_id = h.id INNER JOIN ( SELECT f.house_id, f.supervisor_start FROM dj_core_house_flow f LEFT JOIN dj_member m ON f.worker_id = m.id WHERE f.worker_type = 3 AND f.work_type > 1 ) f ON h.id = f.house_id WHERE h.data_status = 0 AND sa.member_id = #{memberId}
AND h.city_id = #{cityId}
AND f.supervisor_start = 1 AND h.visit_state != 2 AND h.visit_state != 3 AND h.visit_state != 4 AND h.visit_state != 5 AND h.pause = 0 UNION ALL SELECT count( 1 ) AS num, '本周新开' AS NAME, 3 AS sortNum, 'iconWork/home_icon_week.png' AS image FROM dj_basics_supervisor_authority sa INNER JOIN dj_house h ON sa.house_id = h.id INNER JOIN ( SELECT house_id, MIN( create_date ) create_date FROM dj_core_house_flow_apply WHERE apply_type = 4 GROUP BY house_id ) b ON b.house_id = h.id AND DATE_FORMAT( b.create_date, '%x年-第%v周' ) = DATE_FORMAT( SYSDATE( ), '%x年-第%v周' ) WHERE h.data_status = 0 AND sa.member_id = #{memberId}
AND h.city_id = #{cityId}
UNION ALL SELECT count( 1 ) AS num, '本月竣工' AS NAME, 4 AS sortNum, 'iconWork/home_icon_month.png' AS image FROM dj_basics_supervisor_authority sa INNER JOIN dj_house h ON sa.house_id = h.id WHERE h.data_status = 0 AND sa.member_id = #{memberId} AND DATE_FORMAT( h.completed_date, '%Y%m' ) = DATE_FORMAT( CURDATE( ), '%Y%m' ) AND h.visit_state = 3
AND h.city_id = #{cityId}
UNION ALL SELECT count( 1 ) AS num, '今日开工' AS NAME, 5 AS sortNum, 'iconWork/home_icon_tadyFinish.png' AS image FROM dj_basics_supervisor_authority sa INNER JOIN dj_house h ON sa.house_id = h.id INNER JOIN ( SELECT house_id, create_date FROM dj_core_house_flow_apply WHERE apply_type = 4 AND DATE_FORMAT( create_date, '%Y-%m-%d' ) = DATE_FORMAT( SYSDATE( ), '%Y-%m-%d' ) GROUP BY house_id ) b ON b.house_id = h.id WHERE h.data_status = 0 AND sa.member_id = #{memberId} AND h.visit_state = 1
AND h.city_id = #{cityId}
UNION ALL SELECT count( 1 ) AS num, '连续3天未开工' AS NAME, 6 AS sortNum, 'iconWork/home_icon_threeday.png' AS image FROM dj_basics_supervisor_authority sa INNER JOIN dj_house h ON sa.house_id = h.id INNER JOIN ( SELECT house_id, create_date FROM dj_core_house_flow_apply WHERE apply_type = 4 GROUP BY house_id ORDER BY create_date DESC ) b ON b.house_id = h.id AND DATE_SUB( CURDATE( ), INTERVAL 3 DAY ) >= b.create_date WHERE h.data_status = 0 AND sa.member_id = #{memberId}
AND h.city_id = #{cityId}
UNION ALL SELECT count( 1 ) AS num, '超期施工' AS NAME, 7 AS sortNum, 'iconWork/home_icon_overdue.png' AS image FROM dj_basics_supervisor_authority sa INNER JOIN dj_house h ON sa.house_id = h.id INNER JOIN ( SELECT house_id, MAX( end_date ) end_date FROM dj_core_house_flow GROUP BY house_id ) a ON a.house_id = h.id WHERE h.data_status = 0 AND sa.member_id = #{memberId}
AND h.city_id = #{cityId}
UNION ALL SELECT count( 1 ) AS num, '维保工地' AS NAME, 8 AS sortNum, 'iconWork/home_icon_tadyFinish.png' AS image FROM dj_basics_supervisor_authority sa INNER JOIN dj_house h ON sa.house_id = h.id INNER JOIN dj_maintenance_record dmr ON dmr.house_id = h.id WHERE h.data_status = 0 AND sa.member_id = #{memberId}
AND h.city_id = #{cityId}
GROUP BY dmr.house_id

select
COUNT(DISTINCT to_days(create_date))
from dj_core_house_flow_apply
where house_id = #{houseId}

    and worker_type = #{workerType}

and apply_type = 4
