mapper.select:
SELECT w.id AS id, w.name AS name, m.mobile AS mobile, w.worker_id AS workerId, w.state AS state, w.money AS money, w.bank_name AS bankName, w.card_number AS cardNumber, w.processing_date AS processingDate, w.create_date AS createDate, w.modify_date AS modifyDate, w.data_status AS dataStatus, w.role_type AS roleType, w.image AS image, w.reason AS reason, w.memo AS memo FROM dj_worker_withdraw_deposit w INNER JOIN dj_member m ON w.worker_id = m.id WHERE w.data_status = 0
and w.state = #{state}
and w.create_date between #{beginDate} and #{endDate}
and ( w.name like CONCAT('%',#{searchKey},'%') or m.mobile like CONCAT('%',#{searchKey},'%'))
ORDER BY w.create_date DESC
