mapper.select:
select

from dj_worker_worker_detail
where 1=1

    and worker_id=#{workerId}


    and house_id=#{houseId}

ORDER BY create_date DESC

        select

        from dj_worker_worker_detail
        where worker_id=#{workerId}
		and date_format(create_date, '%Y-%m') = #{createDate}
		ORDER BY create_date DESC
