mapper.select:
SELECT id , item_sub_id 'itemSubId' , target_type 'targetType' , target_id 'targetId' , target_name 'targetName' , image , sort , click_number 'clickNumber' FROM dj_recommend_target_info
AND item_sub_id = #{itemSubId}
AND target_type = #{targetType}
AND target_name LIKE CONCAT('%',#{targetName},'%')
AND data_status = 0

mapper.update:
update dj_recommend_target_info
set modify_date = #{modifyDate}

    , sort = #{sort}


    , click_number = #{clickNumber}

WHERE
    id = #{id}

mapper.select:
SELECT
    count(*)
FROM
    dj_recommend_target_info
WHERE
    item_sub_id = #{itemSubId}

AND target_type = #{targetType}


AND target_id = #{targetId}
