mapper.select:
SELECT b.`id`, b.`sup_id` AS supId, a.`id` AS shopId, a.`user_id` AS userId, a.`city_id` AS cityId, a.`storefront_name` AS storefrontName, a.`storefront_address` AS storefrontAddress, a.`storefront_desc` AS storefrontDesc, a.`storefront_logo` AS storefrontLogo, a.`storekeeper_name` AS storekeeperName, a.`mobile`, a.`email`, a.`create_date` AS createDate, a.`modify_date` AS modifyDate, a.`data_status` AS dataStatus, b.application_status AS applicationStatus, b.fail_reason AS failReason, b.contract FROM dj_basics_storefront a LEFT JOIN (SELECT * FROM dj_sup_application WHERE sup_id = #{supId}) b ON a.id = b.shop_id where 1=1
and ( a.storefront_name LIKE CONCAT('%',#{searchKey},'%') or a.mobile LIKE CONCAT('%',#{searchKey},'%') )
and b.application_status=1 and b.contract is not null and a.`city_id`=#{cityId}

select ddosi.id,-- 要货单号 IFNULL(ddosi.create_date,'') as createDate,-- 要货时间 IFNULL(ddosi.ask_count,'') as askCount,-- 要货数 IFNULL(ddosi.num,'') as num,-- 发货数 IFNULL(ddosi.receive,'') as receive,-- 收货数 IFNULL(ddosi.return_count,'') as returnCount-- 退货数 from dj_deliver_order_split_item ddosi where 1=1
and ddosi.house_id=#{houseId}
and ddosi.product_id=#{productId}
and ddosi.storefront_id=#{storefrontId}

select IFNULL(dafr.type,'') as type,-- 类型 IFNULL(dafr.house_order_id,'') as houseOrderId,-- 货单号 IFNULL(dafr.money,'') as money,-- 实际支出金额 IFNULL(drmo.actual_total_amount,'') as totalAmount,-- 订单金额 IFNULL(drmo.state,'') as orderStatus, -- 状态 '' as merge from dj_account_flow_record dafr inner join dj_repair_mend_order drmo on drmo.id=dafr.house_order_id where dafr.defined_account_id=#{storefrontId} and dafr.flow_type=1
and dafr.house_order_id=#{orderNumber}
union all select '3' as type,-- 类型 IFNULL(dr.number,'') as houseOrderId,-- 货单号 IFNULL(dr.total_Amount,'') as money,-- 实际支出金额 '' as totalAmount, -- 订单金额 '' as orderStatus, -- 状态 dr.merge from dj_receipt dr inner join dj_sup_application dsa on dsa.sup_id=dr.supplier_id where dr.data_status=0 and dsa.application_status=1 and dsa.data_status=0 and dsa.shop_id=#{storefrontId}
and 1=2
UNION ALL select '4' as type,-- 类型 '' as houseOrderId,-- 货单号 '' as money,-- 实际支出金额 '' as totalAmount, -- 订单金额 dwwd.deposit_status, -- 状态 '' as merge from dj_account_flow_record dafr inner join dj_worker_withdraw_deposit dwwd on dafr.house_order_id = dwwd.id where dafr.data_status = 0 AND dafr.flow_type = 1 and dafr.defined_account_id=#{storefrontId}
and dafr.house_order_id=#{orderNumber}

select

from dj_basics_storefront
where city_id=#{cityId}
and storefront_type=#{storefrontType}
