mapper.select:
SELECT b.`id`, b.`sup_id` AS supId, a.`id` AS shopId, a.`user_id` AS userId, a.`city_id` AS cityId, a.`storefront_name` AS storefrontName, a.`storefront_address` AS storefrontAddress, a.`storefront_desc` AS storefrontDesc, a.`storefront_logo` AS storefrontLogo, a.`storekeeper_name` AS storekeeperName, a.`mobile`, a.`email`, a.`create_date` AS createDate, a.`modify_date` AS modifyDate, a.`data_status` AS dataStatus, b.application_status AS applicationStatus, b.fail_reason AS failReason, b.contract, a.freight, a.below_unit_price FROM dj_basics_storefront a LEFT JOIN (SELECT * FROM dj_sup_application WHERE sup_id = #{supId}) b ON a.id = b.shop_id where 1=1
and ( a.storefront_name LIKE CONCAT('%',#{searchKey},'%') or a.mobile LIKE CONCAT('%',#{searchKey},'%') )
and b.application_status=1 and b.contract is not null and a.`city_id`=#{cityId}

select IFNULL(dfr.house_order_id,'') as houseOrderId,-- 要货单号 IFNULL(dfr.money,'') as money,-- 实际收入金额 IFNULL(ddo.total_amount,'') as totalAmount -- 订单金额 from dj_account_flow_record dfr inner join dj_deliver_order ddo on dfr.house_order_id=ddo.id where 1=1 and flow_type=1 and state in (0,2)
and dfr.house_order_id LIKE CONCAT('%',#{houseOrderId},'%')
and ddo.storefont_id=#{storefrontId}
