mapper.select:
SELECT bsp.id, IFNULL(bsp.sell_price,0.0) as sellPrice, IFNULL(bsp.supplied_num,'') as suppliedNum, IFNULL(bg.NAME,'') AS goodsName, -- 货品名称 IFNULL(bsp.product_name,'') AS productName, -- 商品名称 IFNULL(bpt.value_name_arr,'') AS valueNameArr, -- 属性 IFNULL(bsp.is_shelf_status,null ) AS isShelfStatus ,-- 是否上下架 IFNULL(prod_template_id,'') as prodTemplateId -- 商品模板id FROM dj_basics_storefront_product bsp INNER JOIN dj_basics_product_template bpt ON bsp.prod_template_id = bpt.id INNER JOIN dj_basics_goods bg ON bsp.goods_id = bg.id where 1=1 and bsp.data_status=0 and ( bsp.image='' or bsp.detail_image='' or bsp.product_name='' or bsp.sell_price='' ) and bsp.storefront_id=#{storefrontId}
and bsp.city_id=#{cityId}
and ( bg.NAME LIKE CONCAT('%',#{keyWord},'%') or bsp.product_name LIKE CONCAT('%',#{keyWord},'%') )

SELECT bsp.id, bg. NAME AS goodsName, -- 货品名称 bsp.product_name AS productName, -- 商品名称 bpt.value_name_arr AS valueNameArr, -- 属性 IFNULL(bsp.is_shelf_status,null ) AS isShelfStatus ,-- 是否上下架 prod_template_id as prodTemplateId -- 商品模板id FROM dj_basics_storefront_product bsp INNER JOIN dj_basics_product_template bpt ON bsp.prod_template_id = bpt.id INNER JOIN dj_basics_goods bg ON bsp.goods_id = bg.id where 1=1 and bsp.data_status=0 and ( bsp.image <> '' or bsp.detail_image <> '' or bsp.product_name <> '' or bsp.sell_price <> '' ) and bsp.storefront_id=#{storefrontId}
and bsp.city_id=#{cityId}
and ( bg.NAME LIKE CONCAT('%',#{keyWord},'%') or bsp.product_name LIKE CONCAT('%',#{keyWord},'%') )
