mapper.select:
select sp.id, sp.product_id AS productId, sp.sup_id AS supId, sp.price, sp.stock, sp.supply_relationship AS supplyRelationShip, sp.goods_id AS goodsId, sp.create_date AS createDate, sp.modify_date AS modifyDate, sp.data_status AS dataStatus FROM dj_sup_application_product sp WHERE sp.supply_relationship = 0
and sp.product_id =#{productId}
ORDER BY sp.price DESC
