mapper.select:
SELECT sap.id, sap.goods_id AS goodsId, sap.product_id AS productId, bg. NAME AS goodsName, bpt. NAME AS productName, bpt.image, bg.attribute_id_arr AS attributeIdArr, bpt.value_name_arr AS valueNameArr, bpt.value_id_arr as valueIdArr, sap.price, sap.stock, bpt.price AS salesPrice, bpt.cartage_price AS porterage, sap.is_cartage_price AS isCartagePrice, sap.supply_relationship AS supplyRelationship, if(ar.adjust_time is not null and ar.adjust_time!="",ar.adjust_time,current_timestamp()) as adjustTime, if(ar.adjust_price is not null and ar.adjust_price!="",ar.adjust_price,0) as adjustprice, sap.porterage FROM dj_sup_application_product sap INNER JOIN dj_basics_goods bg ON sap.goods_id = bg.id INNER JOIN dj_basics_product_template bpt ON sap.product_id = bpt.id LEFT JOIN (SELECT * FROM (select * from dj_adjust_record order by create_date DESC) b GROUP BY b.application_product_id) ar ON ar.application_product_id=sap.id WHERE 1=1 and sap.sup_id = #{supId} and sap.data_status=0 and sap.shop_id = #{shopId}
and sap.application_status in(${applicationStatus})
and sap.application_status=#{applicationStatus}

SELECT bsp.id, bsp.goods_id AS goodsId, bsp.prod_template_id AS productId, bg.NAME AS goodsName, bsp.product_name AS productName, bsp.image, bg.attribute_id_arr AS attributeIdArr, bpt.value_name_arr AS valueNameArr, bpt.value_id_arr AS valueIdArr, bsp.sell_price AS salesPrice, bsp.supplied_num AS stock, bpt.price AS price, bpt.cartage_price AS cartagePrice, bg.is_elevator_fee AS isCartagePrice FROM dj_basics_storefront_product bsp INNER JOIN dj_basics_goods bg ON bsp.goods_id = bg.id INNER JOIN dj_basics_product_template bpt ON bsp.prod_template_id = bpt.id WHERE 1 = 1 AND bsp.data_status = 0 AND bsp.is_shelf_status=1
and bsp.storefront_id = #{shopId}
and bsp.prod_template_id not in
#{item}
