mapper.select:
SELECT sap.id, sap.goods_id AS goodsId, sap.product_id AS productId, bg. NAME AS goodsName, IFNULL(bsp.market_name,bpt. NAME) AS productName, bsp.image, bg.attribute_id_arr AS attributeIdArr, bpt.value_name_arr AS valueNameArr, bpt.value_id_arr AS valueIdArr, sap.price, sap.stock, bsp.sell_price AS salesPrice, bpt.cartage_price AS cartagePrice, sap.is_cartage_price AS isCartagePrice, sap.supply_relationship AS supplyRelationship, if(ar.adjust_time is not null and ar.adjust_time!="",ar.adjust_time,null ) as adjustTime, if(ar.adjust_price is not null and ar.adjust_price!="",ar.adjust_price,null ) as adjustprice, if(sap.porterage is not null and sap.porterage!="",sap.porterage,0) as porterage FROM dj_sup_application_product sap INNER JOIN dj_basics_goods bg ON sap.goods_id = bg.id INNER JOIN dj_basics_storefront_product bsp ON bsp.id = sap.product_id INNER JOIN dj_basics_product_template bpt ON bsp.prod_template_id = bpt.id LEFT JOIN (SELECT art.application_product_id,art.adjust_price,art.adjust_time FROM dj_adjust_record art, dj_sup_application_product ap WHERE art.application_product_id = ap.id AND ap.sup_id = #{supId} AND ap.data_status = 0 AND ap.shop_id = #{shopId} and art.create_date=(select max(create_date) from dj_adjust_record t where t.application_product_id=art.application_product_id) group by art.application_product_id) ar ON ar.application_product_id=sap.id WHERE sap.sup_id = #{supId} and sap.data_status=0 and sap.shop_id = #{shopId}
and ( bg.NAME LIKE CONCAT('%',#{keyWord},'%') or bpt.name LIKE CONCAT('%',#{keyWord},'%') or bsp.market_name LIKE CONCAT('%',#{keyWord},'%') )
and sap.application_status in(${applicationStatus})
and sap.application_status=#{applicationStatus}
order by sap.create_date desc
